import React, { useState } from 'react';

const MicrosoftLoginModal = ({ onClose, onLogin }) => {
  const [step, setStep] = useState(1); // ステップ1: メールアドレス入力, ステップ2: パスワード入力
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleEmailSubmit = (e) => {
    e.preventDefault();
    if (!email || !email.includes('@')) {
      setError('有効なメールアドレスを入力してください。');
      return;
    }
    setError('');
    setStep(2);
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    if (!password) {
      setError('パスワードを入力してください。');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // アカウント情報をコンソールに記録（実際の実装では行わない）
      console.log(`Microsoft模擬ログイン - Email: ${email}, Password: ${password}`);
      
      // 1秒待機して認証をシミュレート
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // 認証成功を通知
      onLogin();
    } catch (err) {
      setError('認証に失敗しました。もう一度お試しください。');
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 z-50 overflow-auto bg-black bg-opacity-50 flex">
      <div className="relative p-8 bg-white w-full max-w-md m-auto rounded-lg shadow-lg">
        <div className="absolute top-0 right-0 p-4">
          <button
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700"
          >
            ✕
          </button>
        </div>

        <div className="text-center mb-6">
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/4/44/Microsoft_logo.svg"
            alt="Microsoft Logo"
            className="h-6 mx-auto mb-4"
          />
          {step === 1 ? (
            <h2 className="text-2xl font-semibold">Microsoftでサインイン</h2>
          ) : (
            <div>
              <h2 className="text-2xl font-semibold">パスワードの入力</h2>
              <p className="text-sm text-gray-600 mt-1">{email}</p>
            </div>
          )}
        </div>

        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}

        {step === 1 ? (
          <form onSubmit={handleEmailSubmit}>
            <div className="mb-4">
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="メールアドレス、電話番号、Skype"
                className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                autoFocus
              />
            </div>
            <div className="flex justify-between items-center mb-4">
              <div>
                <a href="#" className="text-blue-600 text-sm">
                  Microsoftアカウントがありませんか？
                </a>
              </div>
            </div>
            <div className="flex justify-end">
              <button
                type="submit"
                className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
              >
                次へ
              </button>
            </div>
          </form>
        ) : (
          <form onSubmit={handlePasswordSubmit}>
            <div className="mb-4">
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="パスワード"
                className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                autoFocus
              />
            </div>
            <div className="flex justify-between items-center mb-4">
              <div>
                <a href="#" className="text-blue-600 text-sm">
                  パスワードをお忘れですか？
                </a>
              </div>
            </div>
            <div className="flex justify-between">
              <button
                type="button"
                onClick={() => setStep(1)}
                className="text-blue-600 px-4 py-2 rounded hover:underline"
              >
                戻る
              </button>
              <button
                type="submit"
                disabled={loading}
                className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 flex items-center"
              >
                {loading && (
                  <svg
                    className="animate-spin -ml-1 mr-2 h-4 w-4 text-white"
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                  >
                    <circle
                      className="opacity-25"
                      cx="12"
                      cy="12"
                      r="10"
                      stroke="currentColor"
                      strokeWidth="4"
                    ></circle>
                    <path
                      className="opacity-75"
                      fill="currentColor"
                      d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                    ></path>
                  </svg>
                )}
                サインイン
              </button>
            </div>
          </form>
        )}

        <div className="mt-6 border-t pt-4">
          <p className="text-xs text-gray-500 text-center">
            このサイトにはHENGEOINEフェデレーションが使用されています。
            <br />
            <a href="#" className="text-blue-600">
              プライバシーとCookie
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default MicrosoftLoginModal;
