import React, { useState, useEffect } from 'react';
import { 
  UsersIcon, 
  ArrowPathIcon, 
  PlusIcon,
  PencilIcon,
  TrashIcon,
  CheckCircleIcon,
  XCircleIcon,
  UserPlusIcon,
  KeyIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../contexts/AuthContext';

// ユーザーロールのバッジコンポーネント
const RoleBadge = ({ role }) => {
  let color = 'bg-secondary-100 text-secondary-800';
  
  if (role === 'グローバル管理者') {
    color = 'bg-danger-100 text-danger-800';
  } else if (role === '一般ユーザー') {
    color = 'bg-primary-100 text-primary-800';
  } else if (role === 'ゲスト') {
    color = 'bg-warning-100 text-warning-800';
  }
  
  return (
    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${color}`}>
      {role}
    </span>
  );
};

const UserManagement = () => {
  // AuthContextからユーザーロールを取得
  const { user: currentUser } = useAuth();
  
  // モックユーザー一覧（実際のアプリではAPIから取得します）
  const [users, setUsers] = useState([
    {
      id: '1',
      first_name: '太郎',
      last_name: '山田',
      email: 'taro.yamada@example.com',
      role: 'グローバル管理者',
      department: 'IT部門',
      permissions: ['admin', 'read', 'write', 'api_management'],
      status: 'アクティブ',
      auth_type: 'ローカル認証'
    },
    {
      id: '2',
      first_name: '一郎',
      last_name: '鈴木',
      email: 'ichiro.suzuki@example.com',
      role: '一般ユーザー',
      department: 'IT部門',
      permissions: ['read', 'write'],
      status: 'アクティブ',
      auth_type: 'シングルサインオン'
    },
    {
      id: '3',
      first_name: '次郎',
      last_name: '佐藤',
      email: 'jiro.sato@example.com',
      role: 'ゲスト',
      department: '営業部',
      permissions: ['read'],
      status: 'アクティブ',
      auth_type: 'ローカル認証'
    },
    {
      id: '4',
      first_name: '花子',
      last_name: '田中',
      email: 'hanako.tanaka@example.com',
      role: '一般ユーザー',
      department: '総務部',
      permissions: ['read', 'write'],
      status: '無効',
      auth_type: 'シングルサインオン'
    }
  ]);
  
  // 検索とフィルタリング
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  
  // フィルタリング適用
  const filteredUsers = users.filter(user => {
    const nameMatch = `${user.last_name}${user.first_name}${user.email}`.toLowerCase().includes(searchTerm.toLowerCase());
    const roleMatch = roleFilter === 'all' || user.role === roleFilter;
    const statusMatch = statusFilter === 'all' || 
                      (statusFilter === 'active' && user.status === 'アクティブ') ||
                      (statusFilter === 'inactive' && user.status === '無効');
    
    return nameMatch && roleMatch && statusMatch;
  });
  
  return (
    <div className="space-y-6 animate-fade-in">
      {/* ヘッダー */}
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold text-secondary-900">ユーザー管理</h1>
        <div className="flex items-center text-primary-600">
          <ArrowPathIcon className="h-5 w-5 mr-2" />
          <span className="text-sm">最終更新: 2025/03/13 18:25</span>
        </div>
      </div>
      
      {/* 検索とフィルター */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between space-y-2 md:space-y-0">
        <div className="flex-1 max-w-lg">
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <svg className="h-5 w-5 text-secondary-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clipRule="evenodd" />
              </svg>
            </div>
            <input
              type="text"
              placeholder="ユーザーを検索..."
              className="form-input pl-10"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
        
        <div className="flex space-x-2">
          <select 
            className="form-select"
            value={roleFilter}
            onChange={(e) => setRoleFilter(e.target.value)}
          >
            <option value="all">すべてのロール</option>
            <option value="グローバル管理者">グローバル管理者</option>
            <option value="一般ユーザー">一般ユーザー</option>
            <option value="ゲスト">ゲスト</option>
          </select>
          
          <select 
            className="form-select"
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
          >
            <option value="all">すべての状態</option>
            <option value="active">アクティブ</option>
            <option value="inactive">無効</option>
          </select>
        </div>
      </div>
      
      {/* ユーザー情報テーブル */}
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-secondary-200">
          <thead className="bg-secondary-50">
            <tr>
              {['氏名', 'メールアドレス', '部署', 'ロール', '状態', '認証タイプ', 'アクション'].map((col, idx) => (
                <th
                  key={idx}
                  scope="col"
                  className="px-6 py-3 text-left text-xs font-medium text-secondary-500 uppercase tracking-wider"
                >
                  {col}
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-secondary-200">
            {filteredUsers.map(user => (
              <tr key={user.id} className="hover:bg-secondary-50">
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="font-medium text-secondary-900">
                    {user.last_name} {user.first_name}
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-secondary-500">{user.email}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-secondary-500">{user.department}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <RoleBadge role={user.role} />
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span
                    className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                      user.status === 'アクティブ'
                        ? 'bg-success-100 text-success-800'
                        : 'bg-secondary-100 text-secondary-800'
                    }`}
                  >
                    {user.status}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-secondary-500">
                  {user.auth_type}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-secondary-500">
                  <div className="flex space-x-2">
                    <button
                      className="text-primary-600 hover:text-primary-900"
                      title="編集"
                    >
                      <PencilIcon className="h-5 w-5" />
                    </button>
                    <button
                      className="text-primary-600 hover:text-primary-900"
                      title="権限設定"
                    >
                      <KeyIcon className="h-5 w-5" />
                    </button>
                    <button
                      className={`${
                        user.status === 'アクティブ' ? 'text-danger-600 hover:text-danger-900' : 'text-success-600 hover:text-success-900'
                      }`}
                      title={user.status === 'アクティブ' ? '無効化' : '有効化'}
                    >
                      {user.status === 'アクティブ' ? (
                        <XCircleIcon className="h-5 w-5" />
                      ) : (
                        <CheckCircleIcon className="h-5 w-5" />
                      )}
                    </button>
                    <button
                      className="text-danger-600 hover:text-danger-900"
                      title="削除"
                    >
                      <TrashIcon className="h-5 w-5" />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default UserManagement;
