import React, { useState } from 'react';
import { useNavigate, Navigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const { login, isAuthenticated, error } = useAuth();
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // すでに認証されている場合はダッシュボードへリダイレクト
  if (isAuthenticated) {
    return <Navigate to="/" />;
  }

  const onSubmit = async (data) => {
    setLoading(true);
    const success = await login(data.username, data.password);
    setLoading(false);
    
    if (success) {
      navigate('/');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h1 className="text-center text-3xl font-bold text-primary-600">ITSM</h1>
          <h2 className="mt-2 text-center text-xl font-bold text-secondary-900">
            ITサービス管理システム
          </h2>
          <p className="mt-2 text-center text-sm text-secondary-600">
            ISO 20000 / ISO 27001 / ISO 27002 準拠
          </p>
        </div>
        
        <form className="mt-8 space-y-6" onSubmit={handleSubmit(onSubmit)}>
          <div className="rounded-md shadow-sm -space-y-px">
            <div>
              <label htmlFor="username" className="sr-only">
                ユーザー名
              </label>
              <input
                id="username"
                type="text"
                {...register('username', { required: 'ユーザー名は必須です' })}
                className="form-input rounded-t-md"
                placeholder="ユーザー名"
              />
              {errors.username && (
                <p className="form-error">{errors.username.message}</p>
              )}
            </div>
            <div>
              <label htmlFor="password" className="sr-only">
                パスワード
              </label>
              <input
                id="password"
                type="password"
                {...register('password', { required: 'パスワードは必須です' })}
                className="form-input rounded-b-md"
                placeholder="パスワード"
              />
              {errors.password && (
                <p className="form-error">{errors.password.message}</p>
              )}
            </div>
          </div>

          {error && (
            <div className="bg-danger-50 p-3 rounded-md">
              <p className="text-sm text-danger-700">{error}</p>
            </div>
          )}

          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <input
                id="remember_me"
                type="checkbox"
                className="h-4 w-4 text-primary-600 border-secondary-300 rounded"
              />
              <label htmlFor="remember_me" className="ml-2 block text-sm text-secondary-700">
                ログイン状態を保持
              </label>
            </div>

            <div className="text-sm">
              <a href="#forgot-password" className="text-primary-600 hover:text-primary-500">
                パスワードをお忘れですか？
              </a>
            </div>
          </div>

          <div>
            <button
              type="submit"
              disabled={loading}
              className="btn btn-primary w-full flex justify-center"
            >
              {loading ? (
                <svg className="animate-spin h-5 w-5 mr-3" viewBox="0 0 24 24">
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                    fill="none"
                  />
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  />
                </svg>
              ) : null}
              ログイン
            </button>
          </div>
        </form>
        
        <div className="mt-6">
          <p className="text-center text-xs text-secondary-500">
            &copy; 2025 ITサービス管理システム - All rights reserved.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
