import React, { useState } from 'react';
import { 
  Cog6ToothIcon, 
  UserIcon, 
  KeyIcon,
  ShieldCheckIcon,
  ArrowPathIcon,
  UserPlusIcon,
  ServerIcon,
  BellAlertIcon,
  CommandLineIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../contexts/AuthContext';

// 設定タブコンポーネント
const SettingsTab = ({ icon: Icon, label, active, onClick }) => {
  return (
    <div 
      className={`flex items-center px-4 py-3 cursor-pointer border-l-4 ${
        active ? 'border-primary-500 bg-primary-50 text-primary-700' : 'border-transparent hover:bg-secondary-50'
      }`}
      onClick={onClick}
    >
      <Icon className={`h-5 w-5 mr-3 ${active ? 'text-primary-500' : 'text-secondary-500'}`} />
      <span className={`${active ? 'font-medium' : ''}`}>{label}</span>
    </div>
  );
};

// 設定セクションヘッダーコンポーネント
const SectionHeader = ({ title }) => (
  <h3 className="text-lg font-medium text-secondary-900 mb-4">{title}</h3>
);

// 設定トグルコンポーネント
const SettingToggle = ({ label, description, enabled, onChange }) => (
  <div className="flex items-start justify-between py-4 border-b border-secondary-200 last:border-0">
    <div className="flex-1 pr-4">
      <p className="font-medium text-secondary-900">{label}</p>
      {description && <p className="text-sm text-secondary-500 mt-1">{description}</p>}
    </div>
    <div className="flex items-center h-6">
      <button 
        type="button"
        className={`relative inline-flex h-6 w-11 flex-shrink-0 cursor-pointer rounded-full border-2 border-transparent transition-colors duration-200 focus:outline-none ${enabled ? 'bg-primary-600' : 'bg-secondary-200'}`}
        onClick={() => onChange(!enabled)}
      >
        <span 
          className={`pointer-events-none inline-block h-5 w-5 transform rounded-full bg-white shadow ring-0 transition duration-200 ${enabled ? 'translate-x-5' : 'translate-x-0'}`}
        />
      </button>
    </div>
  </div>
);

// 設定選択コンポーネント
const SettingSelect = ({ label, description, value, options, onChange }) => (
  <div className="flex flex-col py-4 border-b border-secondary-200 last:border-0">
    <div className="mb-2">
      <p className="font-medium text-secondary-900">{label}</p>
      {description && <p className="text-sm text-secondary-500 mt-1">{description}</p>}
    </div>
    <div>
      <select 
        className="form-input mt-1"
        value={value}
        onChange={(e) => onChange(e.target.value)}
      >
        {options.map((option) => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
    </div>
  </div>
);

// ユーザー行コンポーネント
const UserRow = ({ user, onEdit }) => (
  <div className="flex items-center justify-between py-4 border-b border-secondary-200 last:border-0">
    <div className="flex items-center">
      <div className="bg-primary-100 p-2 rounded-full">
        <UserIcon className="h-5 w-5 text-primary-600" />
      </div>
      <div className="ml-3">
        <p className="font-medium text-secondary-900">{user.name}</p>
        <p className="text-sm text-secondary-500">{user.email}</p>
      </div>
    </div>
    <div className="flex items-center">
      <span className={`badge ${user.active ? 'bg-success-100 text-success-800' : 'bg-secondary-100 text-secondary-800'}`}>
        {user.active ? '有効' : '無効'}
      </span>
      <span className="mx-3 text-secondary-300">|</span>
      <span className="badge bg-primary-100 text-primary-800">
        {user.role}
      </span>
      <button 
        className="ml-4 text-primary-600 hover:text-primary-700"
        onClick={() => onEdit(user)}
      >
        編集
      </button>
    </div>
  </div>
);

// APIキー行コンポーネント
const ApiKeyRow = ({ apiKey, onRevoke }) => (
  <div className="flex items-center justify-between py-4 border-b border-secondary-200 last:border-0">
    <div className="flex items-center">
      <div className="bg-primary-100 p-2 rounded-full">
        <KeyIcon className="h-5 w-5 text-primary-600" />
      </div>
      <div className="ml-3">
        <p className="font-medium text-secondary-900">{apiKey.name}</p>
        <div className="flex items-center mt-1">
          <p className="text-sm text-secondary-500 mr-2">最終使用: {apiKey.lastUsed}</p>
          <span className={`text-xs px-2 py-0.5 rounded-full ${apiKey.active ? 'bg-success-100 text-success-800' : 'bg-secondary-100 text-secondary-800'}`}>
            {apiKey.active ? '有効' : '無効'}
          </span>
        </div>
      </div>
    </div>
    <div className="flex items-center">
      <span className="text-xs text-secondary-500 mr-4">作成日: {apiKey.created}</span>
      <button 
        className="text-danger-600 hover:text-danger-700"
        onClick={() => onRevoke(apiKey)}
      >
        無効化
      </button>
    </div>
  </div>
);

const Settings = () => {
  // 現在のタブ
  const [activeTab, setActiveTab] = useState('general');
  
  // 一般設定の状態
  const [settings, setSettings] = useState({
    enableNotifications: true,
    emailNotifications: true,
    slackNotifications: false,
    teamsNotifications: false,
    autoBackup: true,
    backupLocation: 'cloud',
    language: 'ja',
    theme: 'light',
    dataRetention: '90',
    timezone: 'Asia/Tokyo'
  });
  
  // ユーザー一覧
  const [users, setUsers] = useState([
    {
      id: 1, 
      name: '山田 太郎', 
      email: 'taro.yamada@example.com', 
      role: '管理者', 
      active: true,
      department: 'IT部門',
      lastLogin: '2025/03/12 15:45'
    },
    {
      id: 2, 
      name: '鈴木 一郎', 
      email: 'ichiro.suzuki@example.com', 
      role: 'オペレーター', 
      active: true,
      department: 'IT部門',
      lastLogin: '2025/03/13 10:22'
    },
    {
      id: 3, 
      name: '佐藤 次郎', 
      email: 'jiro.sato@example.com', 
      role: '閲覧者', 
      active: true,
      department: '経営管理部',
      lastLogin: '2025/03/11 09:15'
    },
    {
      id: 4, 
      name: '田中 三郎', 
      email: 'saburo.tanaka@example.com', 
      role: 'オペレーター', 
      active: false,
      department: '営業部',
      lastLogin: '2025/02/28 14:30'
    }
  ]);

  // APIキー一覧
  const [apiKeys, setApiKeys] = useState([
    {
      id: 1,
      name: 'インシデント自動登録API',
      key: '••••••••••••••••',
      created: '2025/01/15',
      lastUsed: '2025/03/13 09:45',
      active: true
    },
    {
      id: 2,
      name: 'レポート生成API',
      key: '••••••••••••••••',
      created: '2025/02/20',
      lastUsed: '2025/03/12 18:30',
      active: true
    },
    {
      id: 3,
      name: 'テスト環境API',
      key: '••••••••••••••••',
      created: '2025/03/01',
      lastUsed: '2025/03/10 11:15',
      active: false
    }
  ]);

  // 編集中のユーザー
  const [editingUser, setEditingUser] = useState(null);
  
  // APIキー無効化の確認
  const [confirmRevoke, setConfirmRevoke] = useState(null);

  // バックアップ設定と履歴
  const backupOptions = [
    { value: 'cloud', label: 'クラウドストレージ' },
    { value: 'local', label: 'ローカルディレクトリ' },
    { value: 'nas', label: 'ネットワークストレージ' }
  ];
  
  const backupHistory = [
    { date: '2025/03/13 01:00', size: '42.8 MB', status: '成功' },
    { date: '2025/03/12 01:00', size: '42.5 MB', status: '成功' },
    { date: '2025/03/11 01:00', size: '42.3 MB', status: '成功' },
    { date: '2025/03/10 01:00', size: '42.1 MB', status: '成功' },
  ];

  // 言語オプション
  const languageOptions = [
    { value: 'ja', label: '日本語' },
    { value: 'en', label: '英語' }
  ];
  
  // タイムゾーンオプション
  const timezoneOptions = [
    { value: 'Asia/Tokyo', label: '東京 (UTC+9:00)' },
    { value: 'Asia/Osaka', label: '大阪 (UTC+9:00)' },
    { value: 'America/New_York', label: 'ニューヨーク (UTC-5:00)' },
    { value: 'Europe/London', label: 'ロンドン (UTC+0:00)' }
  ];
  
  // データ保持期間オプション
  const dataRetentionOptions = [
    { value: '30', label: '30日間' },
    { value: '90', label: '90日間' },
    { value: '180', label: '180日間' },
    { value: '365', label: '1年間' }
  ];

  // 設定変更ハンドラー
  const handleSettingChange = (key, value) => {
    setSettings(prev => ({
      ...prev,
      [key]: value
    }));
  };

  // ユーザー編集ハンドラー
  const handleEditUser = (user) => {
    setEditingUser(user);
  };

  // APIキー無効化ハンドラー
  const handleRevokeApiKey = (apiKey) => {
    setConfirmRevoke(apiKey);
  };

  // APIキー無効化確認
  const confirmRevokeApiKey = () => {
    setApiKeys(prev => 
      prev.map(key => 
        key.id === confirmRevoke.id ? { ...key, active: false } : key
      )
    );
    setConfirmRevoke(null);
  };

  // APIキー無効化キャンセル
  const cancelRevokeApiKey = () => {
    setConfirmRevoke(null);
  };

  // ユーザー編集モーダルコンポーネント
  const UserEditModal = () => {
    if (!editingUser) return null;

    return (
      <div className="fixed inset-0 z-30 overflow-y-auto">
        <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
          <div className="fixed inset-0 transition-opacity" aria-hidden="true" onClick={() => setEditingUser(null)}>
            <div className="absolute inset-0 bg-secondary-900 opacity-50"></div>
          </div>

          <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>

          <div 
            className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
              <div className="sm:flex sm:items-start">
                <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left w-full">
                  <h3 className="text-lg leading-6 font-medium text-secondary-900">
                    ユーザー編集
                  </h3>
                  
                  <div className="mt-4 space-y-4">
                    <div>
                      <label htmlFor="user-name" className="block text-sm font-medium text-secondary-700">
                        名前
                      </label>
                      <input
                        type="text"
                        id="user-name"
                        className="form-input mt-1 block w-full"
                        value={editingUser.name}
                        onChange={(e) => setEditingUser({...editingUser, name: e.target.value})}
                      />
                    </div>
                    
                    <div>
                      <label htmlFor="user-email" className="block text-sm font-medium text-secondary-700">
                        メールアドレス
                      </label>
                      <input
                        type="email"
                        id="user-email"
                        className="form-input mt-1 block w-full"
                        value={editingUser.email}
                        onChange={(e) => setEditingUser({...editingUser, email: e.target.value})}
                      />
                    </div>
                    
                    <div>
                      <label htmlFor="user-role" className="block text-sm font-medium text-secondary-700">
                        役割
                      </label>
                      <select
                        id="user-role"
                        className="form-input mt-1 block w-full"
                        value={editingUser.role}
                        onChange={(e) => setEditingUser({...editingUser, role: e.target.value})}
                      >
                        <option value="管理者">管理者</option>
                        <option value="オペレーター">オペレーター</option>
                        <option value="閲覧者">閲覧者</option>
                      </select>
                    </div>
                    
                    <div>
                      <label htmlFor="user-department" className="block text-sm font-medium text-secondary-700">
                        部署
                      </label>
                      <input
                        type="text"
                        id="user-department"
                        className="form-input mt-1 block w-full"
                        value={editingUser.department}
                        onChange={(e) => setEditingUser({...editingUser, department: e.target.value})}
                      />
                    </div>
                    
                    <div className="flex items-center">
                      <input
                        id="user-active"
                        type="checkbox"
                        className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-secondary-300 rounded"
                        checked={editingUser.active}
                        onChange={(e) => setEditingUser({...editingUser, active: e.target.checked})}
                      />
                      <label htmlFor="user-active" className="ml-2 block text-sm text-secondary-900">
                        アカウント有効
                      </label>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="bg-secondary-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
              <button
                type="button"
                className="btn btn-primary w-full sm:w-auto sm:ml-3"
                onClick={() => {
                  setUsers(users.map(u => u.id === editingUser.id ? editingUser : u));
                  setEditingUser(null);
                }}
              >
                保存
              </button>
              <button
                type="button"
                className="btn border border-secondary-300 bg-white text-secondary-700 hover:bg-secondary-50 mt-3 sm:mt-0 w-full sm:w-auto"
                onClick={() => setEditingUser(null)}
              >
                キャンセル
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // APIキー無効化確認モーダル
  const ConfirmRevokeModal = () => {
    if (!confirmRevoke) return null;

    return (
      <div className="fixed inset-0 z-30 overflow-y-auto">
        <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
          <div className="fixed inset-0 transition-opacity" aria-hidden="true" onClick={cancelRevokeApiKey}>
            <div className="absolute inset-0 bg-secondary-900 opacity-50"></div>
          </div>

          <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>

          <div 
            className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
              <div className="sm:flex sm:items-start">
                <div className="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full bg-danger-100 sm:mx-0 sm:h-10 sm:w-10">
                  <KeyIcon className="h-6 w-6 text-danger-600" />
                </div>
                <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
                  <h3 className="text-lg leading-6 font-medium text-secondary-900">
                    APIキーの無効化
                  </h3>
                  <div className="mt-2">
                    <p className="text-sm text-secondary-500">
                      「{confirmRevoke.name}」のAPIキーを無効化します。この操作は元に戻せません。
                      このAPIキーを使用しているすべてのアプリケーションが機能しなくなります。
                    </p>
                  </div>
                </div>
              </div>
            </div>
            <div className="bg-secondary-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
              <button
                type="button"
                className="btn btn-danger w-full sm:w-auto sm:ml-3"
                onClick={confirmRevokeApiKey}
              >
                無効化する
              </button>
              <button
                type="button"
                className="btn border border-secondary-300 bg-white text-secondary-700 hover:bg-secondary-50 mt-3 sm:mt-0 w-full sm:w-auto"
                onClick={cancelRevokeApiKey}
              >
                キャンセル
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="space-y-6 animate-fade-in">
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold text-secondary-900">設定</h1>
        <div className="flex items-center text-primary-600">
          <ArrowPathIcon className="h-5 w-5 mr-2" />
          <span className="text-sm">最終更新: 2025/03/13 18:25</span>
        </div>
      </div>

      <div className="flex flex-col md:flex-row bg-white rounded-lg shadow-sm overflow-hidden">
        {/* 設定タブナビゲーション */}
        <div className="w-full md:w-64 bg-secondary-50 border-b md:border-b-0 md:border-r border-secondary-200">
          <SettingsTab 
            icon={Cog6ToothIcon} 
            label="一般設定" 
            active={activeTab === 'general'} 
            onClick={() => setActiveTab('general')} 
          />
          <SettingsTab 
            icon={UserIcon} 
            label="ユーザー管理" 
            active={activeTab === 'users'} 
            onClick={() => setActiveTab('users')} 
          />
          <SettingsTab 
            icon={KeyIcon} 
            label="APIキー" 
            active={activeTab === 'api'} 
            onClick={() => setActiveTab('api')} 
          />
          <SettingsTab 
            icon={ServerIcon} 
            label="バックアップ" 
            active={activeTab === 'backup'} 
            onClick={() => setActiveTab('backup')} 
          />
          <SettingsTab 
            icon={ShieldCheckIcon} 
            label="セキュリティ" 
            active={activeTab === 'security'} 
            onClick={() => setActiveTab('security')} 
          />
        </div>

        {/* 設定内容 */}
        <div className="flex-1 p-6">
          {/* 一般設定 */}
          {activeTab === 'general' && (
            <div className="space-y-6">
              <SectionHeader title="一般設定" />
              
              <div className="card">
                <SectionHeader title="通知設定" />
                <SettingToggle 
                  label="通知を有効にする" 
                  description="システムからの通知を受け取る" 
                  enabled={settings.enableNotifications} 
                  onChange={(value) => handleSettingChange('enableNotifications', value)} 
                />
                <SettingToggle 
                  label="メール通知" 
                  description="重要なイベントやアラートをメールで受け取る" 
                  enabled={settings.emailNotifications} 
                  onChange={(value) => handleSettingChange('emailNotifications', value)} 
                />
                <SettingToggle 
                  label="Slack通知" 
                  description="Slackチャンネルに通知を送信する" 
                  enabled={settings.slackNotifications} 
                  onChange={(value) => handleSettingChange('slackNotifications', value)} 
                />
                <SettingToggle 
                  label="Microsoft Teams通知" 
                  description="Microsoft Teamsチャネルに通知を送信する" 
                  enabled={settings.teamsNotifications} 
                  onChange={(value) => handleSettingChange('teamsNotifications', value)} 
                />
              </div>
              
              <div className="card">
                <SectionHeader title="システム設定" />
                <SettingSelect 
                  label="言語" 
                  description="インターフェースの表示言語" 
                  value={settings.language} 
                  options={languageOptions} 
                  onChange={(value) => handleSettingChange('language', value)} 
                />
                <SettingSelect 
                  label="タイムゾーン" 
                  description="表示されるすべての日時に適用されます" 
                  value={settings.timezone} 
                  options={timezoneOptions} 
                  onChange={(value) => handleSettingChange('timezone', value)} 
                />
                <SettingSelect 
                  label="データ保持期間" 
                  description="ログとイベントデータを保持する期間" 
                  value={settings.dataRetention} 
                  options={dataRetentionOptions} 
                  onChange={(value) => handleSettingChange('dataRetention', value)} 
                />
              </div>
            </div>
          )}

          {/* ユーザー管理 */}
          {activeTab === 'users' && (
            <div className="space-y-6">
              <div className="flex items-center justify-between">
                <SectionHeader title="ユーザー管理" />
                <button className="btn btn-primary">
                  <UserPlusIcon className="h-5 w-5 mr-2" />
                  ユーザー追加
                </button>
              </div>
              
              <div className="card space-y-4">
                <div className="flex justify-between items-center">
                  <p className="text-sm text-secondary-500">全 {users.length} ユーザー中 {users.filter(u => u.active).length} 名がアクティブ</p>
                  <div className="relative w-64">
                    <input 
                      type="text" 
                      placeholder="ユーザーを検索..." 
                      className="form-input pl-10 w-full"
                    />
                    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                      <svg className="h-5 w-5 text-secondary-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                      </svg>
                    </div>
                  </div>
                </div>
                
                <div className="space-y-2">
                  {users.map(user => (
                    <UserRow 
                      key={user.id} 
                      user={user} 
                      onEdit={handleEditUser} 
                    />
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* APIキー */}
          {activeTab === 'api' && (
            <div className="space-y-6">
              <div className="flex items-center justify-between">
                <SectionHeader title="APIキー管理" />
                <button className="btn btn-primary">
                  <KeyIcon className="h-5 w-5 mr-2" />
                  新規APIキー発行
                </button>
              </div>
              
              <div className="card space-y-4">
                <p className="text-sm text-secondary-500">
                  APIキーはシステムへのプログラムによるアクセスを許可します。
                  APIキーは秘密情報として取り扱い、安全に保管してください。
                </p>
                
                <div className="space-y-2">
                  {apiKeys.map(apiKey => (
                    <ApiKeyRow 
                      key={apiKey.id} 
                      apiKey={apiKey} 
                      onRevoke={handleRevokeApiKey} 
                    />
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* バックアップ */}
          {activeTab === 'backup' && (
            <div className="space-y-6">
              <SectionHeader title="バックアップ設定" />
              
              <div className="card">
                <SettingToggle 
                  label="自動バックアップ" 
                  description="毎日深夜にシステムバックアップを実行します" 
                  enabled={settings.autoBackup} 
                  onChange={(value) => handleSettingChange('autoBackup', value)} 
                />
                <SettingSelect 
                  label="バックアップ保存先" 
                  description="バックアップデータの保存場所" 
                  value={settings.backupLocation} 
                  options={backupOptions} 
                  onChange={(value) => handleSettingChange('backupLocation', value)} 
                />
              </div>
              
              <div className="card">
                <div className="flex items-center justify-between mb-4">
                  <h4 className="font-medium text-secondary-900">バックアップ履歴</h4>
                  <button className="btn btn-secondary btn-sm">
                    <ArrowPathIcon className="h-4 w-4 mr-1" />
                    今すぐバックアップ
                  </button>
                </div>
                
                <div className="overflow-x-auto">
                  <table className="min-w-full divide-y divide-secondary-200">
                    <thead className="bg-secondary-50">
                      <tr>
                        <th className="px-4 py-2 text-left text-xs font-medium text-secondary-500 uppercase tracking-wider">日時</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-secondary-500 uppercase tracking-wider">サイズ</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-secondary-500 uppercase tracking-wider">ステータス</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-secondary-500 uppercase tracking-wider">アクション</th>
                      </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-secondary-200">
                      {backupHistory.map((backup, index) => (
                        <tr key={index}>
                          <td className="px-4 py-2 whitespace-nowrap text-sm text-secondary-900">{backup.date}</td>
                          <td className="px-4 py-2 whitespace-nowrap text-sm text-secondary-500">{backup.size}</td>
                          <td className="px-4 py-2 whitespace-nowrap text-sm">
                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                              backup.status === '成功' ? 'bg-success-100 text-success-800' : 'bg-danger-100 text-danger-800'
                            }`}>
                              {backup.status}
                            </span>
                          </td>
                          <td className="px-4 py-2 whitespace-nowrap text-sm text-right">
                            <button className="text-primary-600 hover:text-primary-700">ダウンロード</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}

          {/* セキュリティ設定 */}
          {activeTab === 'security' && (
            <div className="space-y-6">
              <SectionHeader title="セキュリティ設定" />
              
              <div className="card">
                <SectionHeader title="パスワードポリシー" />
                <SettingToggle 
                  label="強力なパスワードを要求" 
                  description="大文字、小文字、数字、特殊文字を含む8文字以上のパスワードを要求" 
                  enabled={true} 
                  onChange={() => {}} 
                />
                <SettingToggle 
                  label="定期的なパスワード変更" 
                  description="90日ごとにパスワード変更を要求" 
                  enabled={true} 
                  onChange={() => {}} 
                />
                <SettingToggle 
                  label="パスワード履歴" 
                  description="直近5回使用したパスワードの再利用を禁止" 
                  enabled={true} 
                  onChange={() => {}} 
                />
              </div>
              
              <div className="card">
                <SectionHeader title="認証設定" />
                <SettingToggle 
                  label="多要素認証（MFA）" 
                  description="ログイン時に追加の認証要素を要求" 
                  enabled={true} 
                  onChange={() => {}} 
                />
                <SettingToggle 
                  label="ログイン試行制限" 
                  description="5回連続で失敗した場合にアカウントをロック" 
                  enabled={true} 
                  onChange={() => {}} 
                />
                <SettingToggle 
                  label="セッションタイムアウト" 
                  description="30分間の非アクティブ後にセッションを終了" 
                  enabled={true} 
                  onChange={() => {}} 
                />
              </div>
              
              <div className="card">
                <SectionHeader title="監査とログ" />
                <SettingToggle 
                  label="アクセスログの記録" 
                  description="すべてのシステムアクセスを記録" 
                  enabled={true} 
                  onChange={() => {}} 
                />
                <SettingToggle 
                  label="変更監査" 
                  description="システム設定やユーザー権限の変更を記録" 
                  enabled={true} 
                  onChange={() => {}} 
                />
                <SettingToggle 
                  label="ログインアラート" 
                  description="異常なログインパターンを検出した場合に通知" 
                  enabled={true} 
                  onChange={() => {}} 
                />
              </div>
            </div>
          )}
        </div>
      </div>

      {/* モーダル */}
      <UserEditModal />
      <ConfirmRevokeModal />
    </div>
  );
};

export default Settings;
