import React from 'react';
import { Link } from 'react-router-dom';
import {
  ChartBarIcon,
  ExclamationTriangleIcon,
  ShieldCheckIcon,
  ServerIcon,
  ClockIcon,
  CheckCircleIcon,
  ArrowPathIcon
} from '@heroicons/react/24/outline';

// ステータスカードコンポーネント
const StatusCard = ({ title, value, icon: Icon, color, description }) => (
  <div className="card">
    <div className="flex items-start justify-between">
      <div>
        <h3 className="text-lg font-semibold text-secondary-800">{title}</h3>
        <p className={`text-3xl font-bold ${color}`}>{value}</p>
        {description && <p className="text-sm text-secondary-500 mt-1">{description}</p>}
      </div>
      <div className={`p-3 rounded-full ${color.replace('text', 'bg').replace('-600', '-100')}`}>
        <Icon className={`h-6 w-6 ${color}`} />
      </div>
    </div>
  </div>
);

// システムステータスコンポーネント
const SystemStatusItem = ({ name, status, uptime }) => {
  const getStatusColor = (status) => {
    switch (status) {
      case '正常':
        return 'bg-success-100 text-success-800';
      case '警告':
        return 'bg-warning-100 text-warning-800';
      case '異常':
        return 'bg-danger-100 text-danger-800';
      default:
        return 'bg-secondary-100 text-secondary-800';
    }
  };

  return (
    <div className="flex items-center justify-between py-3 border-b border-secondary-200 last:border-0">
      <div className="flex items-center">
        <ServerIcon className="h-5 w-5 text-secondary-500 mr-3" />
        <span className="font-medium">{name}</span>
      </div>
      <div className="flex items-center space-x-4">
        {uptime && (
          <div className="flex items-center text-secondary-500 text-sm">
            <ClockIcon className="h-4 w-4 mr-1" />
