import React, { useState, useEffect } from 'react';
import { useNavigate, Navigate, useLocation } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const { login, loginWithMicrosoft, isAuthenticated, error, logout } = useAuth();
  const location = useLocation();
  const [loading, setLoading] = useState(false);
  const [msLoading, setMsLoading] = useState(false);
  const navigate = useNavigate();

  // ログインページに直接アクセスした場合、ログアウト処理を行う
  useEffect(() => {
    // URLパラメータで強制ログアウトを制御
    const queryParams = new URLSearchParams(location.search);
    const keepLoggedIn = queryParams.get('keepLoggedIn') === 'true';
    
    // 直接ログインページにアクセスした場合で、keepLoggedInパラメータがない場合はログアウト
    if (!keepLoggedIn) {
      logout();
    }
  }, [logout, location]);

  // ログイン後はダッシュボードへリダイレクト
  // ただし、初期レンダリング時はuseEffectでログアウト処理が行われるため問題ない
  if (isAuthenticated) {
    // リダイレクト先を指定（state経由で渡されたパスがあればそちらを優先）
    const from = location.state?.from?.pathname || "/";
    return <Navigate to={from} />;
  }

  const onSubmit = async (data) => {
    setLoading(true);
    const success = await login(data.username, data.password);
    setLoading(false);
    
    if (success) {
      navigate('/');
    }
  };

  // 実際のMicrosoftアカウントページにリダイレクト
  const handleMicrosoftLoginClick = () => {
    // 実際のMicrosoftアカウントページを新しいウィンドウで開く
    const microsoftWindow = window.open('https://account.microsoft.com/', '_blank');
    
    // 模擬的にフェデレーション認証が完了したことをシミュレート
    setTimeout(() => {
      // 自動的にダッシュボードにリダイレクト（実際の環境ではOAuthコールバックを使用）
      setMsLoading(true);
      loginWithMicrosoft().then(success => {
        setMsLoading(false);
        if (success) {
          navigate('/dashboard');
        }
      });
      
      // 開いたMicrosoftウィンドウを閉じる（実際の環境では不要）
      if (microsoftWindow) {
        microsoftWindow.close();
      }
    }, 5000); // 5秒後に認証完了をシミュレート
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h1 className="text-center text-3xl font-bold text-primary-600">ITSM</h1>
          <h2 className="mt-2 text-center text-xl font-bold text-secondary-900">
            ITサービス管理システム
          </h2>
          <p className="mt-2 text-center text-sm text-secondary-600">
            ISO 20000 / ISO 27001 / ISO 27002 準拠
          </p>
        </div>
        
        {/* Microsoft認証ボタン */}
        <div>
          <button
            type="button"
            onClick={handleMicrosoftLoginClick}
            disabled={msLoading}
            className="w-full flex justify-center items-center px-4 py-2 border border-secondary-300 rounded-md shadow-sm bg-white hover:bg-secondary-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
          >
            {msLoading ? (
              <svg className="animate-spin h-5 w-5 mr-3" viewBox="0 0 24 24">
                <circle
                  className="opacity-25"
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="currentColor"
                  strokeWidth="4"
                  fill="none"
                />
                <path
                  className="opacity-75"
                  fill="currentColor"
                  d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                />
              </svg>
            ) : (
              <svg
                className="w-5 h-5 mr-2"
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 23 23"
              >
                <path fill="#f3f3f3" d="M0 0h23v23H0z"></path>
                <path fill="#f35325" d="M1 1h10v10H1z"></path>
                <path fill="#81bc06" d="M12 1h10v10H12z"></path>
                <path fill="#05a6f0" d="M1 12h10v10H1z"></path>
                <path fill="#ffba08" d="M12 12h10v10H12z"></path>
              </svg>
            )}
            <span>Microsoftアカウントでログイン</span>
          </button>
        </div>

        
        <div className="relative">
          <div className="absolute inset-0 flex items-center">
            <div className="w-full border-t border-secondary-300"></div>
          </div>
          <div className="relative flex justify-center text-sm">
            <span className="px-2 bg-gray-50 text-secondary-500">または</span>
          </div>
        </div>
        
        <form className="space-y-6" onSubmit={handleSubmit(onSubmit)}>
          <div className="rounded-md shadow-sm -space-y-px">
            <div>
              <label htmlFor="username" className="sr-only">
                ユーザー名
              </label>
              <input
                id="username"
                type="text"
                {...register('username', { required: 'ユーザー名は必須です' })}
                className="form-input rounded-t-md"
                placeholder="ユーザー名"
              />
              {errors.username && (
                <p className="form-error">{errors.username.message}</p>
              )}
            </div>
            <div>
              <label htmlFor="password" className="sr-only">
                パスワード
              </label>
              <input
                id="password"
                type="password"
                {...register('password', { required: 'パスワードは必須です' })}
                className="form-input rounded-b-md"
                placeholder="パスワード"
              />
              {errors.password && (
                <p className="form-error">{errors.password.message}</p>
              )}
            </div>
          </div>

          {error && (
            <div className="bg-danger-50 p-3 rounded-md">
              <p className="text-sm text-danger-700">{error}</p>
            </div>
          )}

          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <input
                id="remember_me"
                type="checkbox"
                className="h-4 w-4 text-primary-600 border-secondary-300 rounded"
              />
              <label htmlFor="remember_me" className="ml-2 block text-sm text-secondary-700">
                ログイン状態を保持
              </label>
            </div>

            <div className="text-sm">
              <a href="#forgot-password" className="text-primary-600 hover:text-primary-500">
                パスワードをお忘れですか？
              </a>
            </div>
          </div>

          <div>
            <button
              type="submit"
              disabled={loading}
              className="btn btn-primary w-full flex justify-center"
            >
              {loading ? (
                <svg className="animate-spin h-5 w-5 mr-3" viewBox="0 0 24 24">
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                    fill="none"
                  />
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  />
                </svg>
              ) : null}
              ログイン
            </button>
          </div>
        </form>
        
        <div className="mt-6">
          <div className="bg-primary-50 border border-primary-200 rounded-md p-3 text-sm">
            <p className="font-medium text-primary-700 mb-1">テスト用アカウント</p>
            <ul className="text-primary-600 text-xs space-y-1">
              <li><span className="font-medium">グローバル管理者:</span> admin / admin</li>
              <li><span className="font-medium">一般ユーザー:</span> user / user</li>
              <li><span className="font-medium">ゲスト:</span> guest / guest</li>
            </ul>
          </div>
          <p className="text-center text-xs text-secondary-500 mt-4">
            &copy; 2025 ITサービス管理システム - All rights reserved.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
