import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const MicrosoftCallback = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { loginWithMicrosoft } = useAuth();
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [authType, setAuthType] = useState('microsoft'); // 'microsoft' または 'hengeoine'
  
  // Microsoft認証設定
  const msAuthConfig = {
    clientId: '00000000-0000-0000-0000-000000000000', // テスト用のダミーID、実際の環境では環境変数から取得
    redirectUri: window.location.origin + '/microsoft-callback',
    authority: 'https://login.microsoftonline.com/common',
    scopes: ['openid', 'profile', 'email', 'User.Read']
  };

  useEffect(() => {
    const handleAuth = async () => {
      try {
        // URLクエリパラメータから認証情報を取得
        const queryParams = new URLSearchParams(location.search);
        const code = queryParams.get('code');
        const authSource = queryParams.get('source'); // 'hengeoine' または undefined（通常のMicrosoft認証）
        const error = queryParams.get('error');
        const errorDescription = queryParams.get('error_description');
        
        // エラーパラメータの確認
        if (error) {
          // AADSTSエラーの処理
          if (error === 'AADSTS900144' || errorDescription?.includes('client_id')) {
            console.error('Microsoft認証エラー: client_idパラメータが必要です');
            
            // 認証URLを再構築して適切なパラメータを含める
            const authUrl = new URL(`${msAuthConfig.authority}/oauth2/v2.0/authorize`);
            authUrl.searchParams.append('client_id', msAuthConfig.clientId);
            authUrl.searchParams.append('response_type', 'code');
            authUrl.searchParams.append('redirect_uri', msAuthConfig.redirectUri);
            authUrl.searchParams.append('scope', msAuthConfig.scopes.join(' '));
            authUrl.searchParams.append('response_mode', 'query');
            authUrl.searchParams.append('state', btoa(JSON.stringify({ timestamp: new Date().getTime() })));
            
            // エラーメッセージを表示
            setError(`認証エラー: ${errorDescription || error}。認証を再試行します。`);
            setLoading(false);
            
            // 1秒後に正しいURLで認証を再試行
            setTimeout(() => {
              window.location.href = authUrl.toString();
            }, 1000);
            return;
          } else {
            throw new Error(errorDescription || `認証エラー: ${error}`);
          }
        }
        
        if (authSource === 'hengeoine') {
          setAuthType('hengeoine');
          console.log('HENGEOINE認証からのコールバックを処理しています');
        }
        
        if (!code) {
          // codeがない場合でも、HENGEOINEからのリダイレクトの可能性がある
          if (authSource === 'hengeoine' || queryParams.get('hengeoine_session')) {
            console.log('HENGEOINEセッション検出:', queryParams.get('hengeoine_session'));
            // HENGEOINEのセッショントークンがある場合は処理を継続
          } else {
            throw new Error('認証コードが取得できませんでした');
          }
        } else {
          // 開発用: コンソールに認証コードを出力
          console.log(`${authType === 'hengeoine' ? 'HENGEOINE' : 'Microsoft'}認証コード:`, code);
        }
        
        // 認証処理
        const success = await loginWithMicrosoft();
        
        if (success) {
          // 認証成功
          navigate('/dashboard');
        } else {
          throw new Error(`${authType === 'hengeoine' ? 'HENGEOINE' : 'Microsoft'}認証に失敗しました`);
        }
      } catch (err) {
        console.error(`${authType === 'hengeoine' ? 'HENGEOINE' : 'Microsoft'}認証エラー:`, err);
        setError(err.message || '認証プロセス中にエラーが発生しました');
        setLoading(false);
      }
    };

    handleAuth();
  }, [loginWithMicrosoft, navigate, location, authType, msAuthConfig]);

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center">
        <div className="mb-4">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-600"></div>
        </div>
        <h2 className="text-xl font-semibold text-secondary-700">
          {authType === 'hengeoine' ? 'HENGEOINE認証' : 'Microsoft認証'}処理中...
        </h2>
        <p className="mt-2 text-secondary-500">しばらくお待ちください</p>
        
        {authType === 'hengeoine' && (
          <div className="mt-4 max-w-md text-center">
            <div className="bg-blue-50 border border-blue-200 rounded p-3">
              <span className="text-xs px-1 py-0.5 bg-blue-100 text-blue-800 rounded-sm">HENGEOINE</span>
              <p className="text-sm text-blue-800 mt-1">
                フェデレーション認証からのコールバックを処理しています
              </p>
            </div>
          </div>
        )}
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center p-4">
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-4 max-w-md">
          <div className="flex items-center mb-2">
            {authType === 'hengeoine' ? (
              <>
                <span className="text-xs px-1 py-0.5 bg-blue-100 text-blue-800 rounded-sm mr-2">HENGEOINE</span>
                <p className="font-bold">フェデレーション認証エラー</p>
              </>
            ) : (
              <p className="font-bold">Microsoft認証エラー</p>
            )}
          </div>
          <p>{error}</p>
          <p className="text-xs mt-2 text-red-600">
            {authType === 'hengeoine' 
              ? 'HENGEOINE認証サーバーからの応答に問題がありました。' 
              : 'Microsoft認証サーバーからの応答に問題がありました。'}
          </p>
        </div>
        <button
          onClick={() => navigate('/login')}
          className="px-4 py-2 bg-primary-600 text-white rounded hover:bg-primary-700"
        >
          ログインページに戻る
        </button>
      </div>
    );
  }

  return null;
};

export default MicrosoftCallback;
