import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// レイアウトコンポーネント
import Layout from './components/layout/Layout';

// ページコンポーネント
import Dashboard from './pages/Dashboard';
import Login from './pages/Login';
import MicrosoftAuth from './pages/MicrosoftAuth';
import SystemMonitoring from './pages/SystemMonitoring';
import IncidentManagement from './pages/IncidentManagement';
import SecurityEvents from './pages/SecurityEvents';
import Reports from './pages/Reports';
import Settings from './pages/Settings';
import NotFound from './pages/NotFound';
import MetricsAnalysis from './pages/MetricsAnalysis';
import UserManagement from './pages/UserManagement';

// 認証コンテキスト
import { AuthProvider, useAuth } from './contexts/AuthContext';

// 認証済みルートのラッパーコンポーネント
const ProtectedRoute = ({ children }) => {
  const { isAuthenticated } = useAuth();
  
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }
  
  return children;
};

function AppRoutes() {
  const { isAuthenticated } = useAuth();
  
  return (
    <Routes>
      {/* 初期ルートを認証状態に基づいてリダイレクト */}
      <Route path="/" element={
        isAuthenticated ? <Navigate to="/dashboard" /> : <Navigate to="/login" />
      } />
      
      {/* ログインページは常に表示する - 内部でログアウト処理を行う */}
      <Route path="/login" element={<Login />} />
      <Route path="/microsoft-auth" element={<MicrosoftAuth />} />
      
      {/* 認証が必要なルート */}
      <Route path="/" element={
        <ProtectedRoute>
          <Layout />
        </ProtectedRoute>
      }>
        <Route path="dashboard" element={<Dashboard />} />
        <Route path="monitoring" element={<SystemMonitoring />} />
        <Route path="incidents" element={<IncidentManagement />} />
        <Route path="security" element={<SecurityEvents />} />
        <Route path="reports" element={<Reports />} />
        <Route path="metrics" element={<MetricsAnalysis />} />
        <Route path="users" element={<UserManagement />} />
        <Route path="settings" element={<Settings />} />
        <Route path="*" element={<NotFound />} />
      </Route>
    </Routes>
  );
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <ToastContainer position="top-right" autoClose={3000} />
        <AppRoutes />
      </Router>
    </AuthProvider>
  );
}

export default App;
