import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const MicrosoftCallback = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { loginWithMicrosoft } = useAuth();
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const handleAuth = async () => {
      try {
        // URLクエリパラメータから認証コードを取得
        const queryParams = new URLSearchParams(location.search);
        const code = queryParams.get('code');
        
        if (!code) {
          throw new Error('認証コードが取得できませんでした');
        }
        
        // 開発用: コンソールに認証コードを出力
        console.log('認証コード:', code);
        
        // 認証処理
        const success = await loginWithMicrosoft();
        
        if (success) {
          // 認証成功
          navigate('/dashboard');
        } else {
          throw new Error('認証に失敗しました');
        }
      } catch (err) {
        console.error('Microsoft認証エラー:', err);
        setError(err.message || '認証プロセス中にエラーが発生しました');
        setLoading(false);
      }
    };

    handleAuth();
  }, [loginWithMicrosoft, navigate, location]);

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center">
        <div className="mb-4">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-600"></div>
        </div>
        <h2 className="text-xl font-semibold text-secondary-700">ログイン処理中...</h2>
        <p className="mt-2 text-secondary-500">しばらくお待ちください</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center p-4">
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-4 max-w-md">
          <p className="font-bold">エラーが発生しました</p>
          <p>{error}</p>
        </div>
        <button
          onClick={() => navigate('/login')}
          className="px-4 py-2 bg-primary-600 text-white rounded hover:bg-primary-700"
        >
          ログインページに戻る
        </button>
      </div>
    );
  }

  return null;
};

export default MicrosoftCallback;
