import React, { createContext, useState, useContext, useEffect } from 'react';

// 認証コンテキストの作成
const AuthContext = createContext();

// 開発用のモックユーザーロール
const USER_ROLES = {
  GLOBAL_ADMIN: 'グローバル管理者',
  GENERAL_USER: '一般ユーザー',
  GUEST: 'ゲスト'
};

// 開発用のモックユーザーデータ
const MOCK_USERS = {
  'admin': {
    id: '1',
    first_name: '太郎',
    last_name: '山田',
    email: 'taro.yamada@example.com',
    role: USER_ROLES.GLOBAL_ADMIN,
    department: 'IT部門',
    permissions: ['admin', 'read', 'write', 'api_management'],
    avatar: 'https://i.pravatar.cc/150?img=1'
  },
  'user': {
    id: '2',
    first_name: '一郎',
    last_name: '鈴木',
    email: 'ichiro.suzuki@example.com',
    role: USER_ROLES.GENERAL_USER,
    department: 'IT部門',
    permissions: ['read', 'write'],
    avatar: 'https://i.pravatar.cc/150?img=2'
  },
  'guest': {
    id: '3',
    first_name: '次郎',
    last_name: '佐藤',
    email: 'jiro.sato@example.com',
    role: USER_ROLES.GUEST,
    department: '営業部',
    permissions: ['read'],
    avatar: 'https://i.pravatar.cc/150?img=3'
  }
};

// 認証プロバイダーコンポーネント
export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 開発モード: ユーザー認証状態の確認
  useEffect(() => {
    const checkAuthStatus = async () => {
      const token = localStorage.getItem('token');
      const userRole = localStorage.getItem('userRole');
      
      if (token && userRole && MOCK_USERS[userRole]) {
        try {
          // 開発モード: モックユーザーデータをセット
          setCurrentUser(MOCK_USERS[userRole]);
        } catch (err) {
          console.error('認証エラー:', err);
          localStorage.removeItem('token');
          localStorage.removeItem('userRole');
          setCurrentUser(null);
        }
      }
      setLoading(false);
    };

    // 1秒後に認証状態チェックを模擬（ロード中の表示確認用）
    setTimeout(() => {
      checkAuthStatus();
    }, 1000);
  }, []);

  // Microsoft認証をシミュレートするログイン関数
  const loginWithMicrosoft = async () => {
    try {
      setError(null);
      setLoading(true);
      
      console.log('Microsoft認証をモックモードで実行中...');
      
      // モック認証: 常に成功するよう設定
      // 開発環境では実際のMicrosoft認証をスキップ
      await new Promise(resolve => setTimeout(resolve, 1000)); // 1秒遅延で認証を模擬
      
      // 管理者アカウントでログイン
      const userRole = 'admin';
      const token = 'mock-ms-token-' + Math.random().toString(36).substring(2);
      
      localStorage.setItem('token', token);
      localStorage.setItem('userRole', userRole);
      setCurrentUser(MOCK_USERS[userRole]);
      setLoading(false);
      
      console.log('Microsoft認証成功（モックモード）');
      return true;
    } catch (err) {
      console.error('Microsoft認証エラー:', err);
      setError('Microsoft認証に失敗しました。再度お試しください。');
      setLoading(false);
      return false;
    }
  };

  // 通常ログイン関数
  const login = async (username, password) => {
    try {
      setError(null);
      
      // ログイン試行をコンソールに出力（デバッグ用）
      console.log(`ログイン試行: username=${username}, password=${password}`);
      
      // 入力チェック
      if (!username || !password) {
        console.error('入力エラー: ユーザー名またはパスワードが入力されていません');
        throw new Error('ユーザー名とパスワードを入力してください');
      }
      
      // テスト用アカウントの検証
      let userRole = null;
      
      // 厳密な比較で検証
      if (username === 'admin' && password === 'admin') {
        console.log('管理者アカウントでのログイン成功');
        userRole = 'admin'; // グローバル管理者
      } else if (username === 'user' && password === 'user') {
        console.log('一般ユーザーアカウントでのログイン成功');
        userRole = 'user'; // 一般ユーザー
      } else if (username === 'guest' && password === 'guest') {
        console.log('ゲストアカウントでのログイン成功');
        userRole = 'guest'; // ゲスト
      } else {
        console.error(`ログイン認証失敗: ユーザー ${username} は認証されませんでした`);
        throw new Error('ユーザー名またはパスワードが正しくありません');
      }
      
      // モックトークンとユーザー情報を設定
      const token = 'mock-jwt-token-' + Math.random().toString(36).substring(2);
      localStorage.setItem('token', token);
      localStorage.setItem('userRole', userRole);
      
      // ユーザー情報を設定
      console.log(`ユーザー情報設定: ${userRole}`, MOCK_USERS[userRole]);
      setCurrentUser(MOCK_USERS[userRole]);
      
      console.log(`ログイン成功: ${username} (${userRole}) - トークン: ${token}`);
      return true;
    } catch (err) {
      console.error('ログインエラー:', err);
      setError(err.message || 'ログインに失敗しました。');
      return false;
    }
  };

  // ログアウト関数
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userRole');
    setCurrentUser(null);
    console.log('ログアウトしました');
  };

  // 権限チェック関数
  const hasPermission = (permission) => {
    if (!currentUser) return false;
    return currentUser.permissions.includes(permission);
  };

  // 提供する値
  const value = {
    currentUser,
    loading,
    error,
    login,
    loginWithMicrosoft,
    logout,
    hasPermission,
    isAuthenticated: !!currentUser,
    USER_ROLES // ロール定数をエクスポート
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// カスタムフック
export const useAuth = () => {
  return useContext(AuthContext);
};
