import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { Bars3Icon, BellIcon, UserCircleIcon } from '@heroicons/react/24/outline';

const Header = ({ toggleSidebar }) => {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <header className="bg-white shadow-sm z-10">
      <div className="flex items-center justify-between px-4 py-3">
        <div className="flex items-center">
          <button
            onClick={toggleSidebar}
            className="p-2 rounded-md text-secondary-500 hover:bg-secondary-100 focus:outline-none"
          >
            <Bars3Icon className="h-6 w-6" />
          </button>
          <h1 className="ml-4 text-lg font-semibold text-secondary-900">ITサービス管理システム</h1>
        </div>

        <div className="flex items-center space-x-4">
          {/* 通知ボタン */}
          <button className="relative p-2 rounded-full text-secondary-500 hover:bg-secondary-100 focus:outline-none">
            <BellIcon className="h-6 w-6" />
            <span className="absolute top-0 right-0 block h-2 w-2 rounded-full bg-danger-500 ring-2 ring-white"></span>
          </button>

          {/* ユーザーメニュー */}
          <div className="relative">
            <div className="flex items-center space-x-2 cursor-pointer">
              <UserCircleIcon className="h-8 w-8 text-secondary-500" />
              <div className="hidden md:block">
                <p className="text-sm font-medium text-secondary-900">{currentUser?.first_name} {currentUser?.last_name}</p>
                <p className="text-xs text-secondary-500">
                  <span className="font-medium">{currentUser?.role}</span>
                  {currentUser && (
                    <span className="ml-1 px-1.5 py-0.5 bg-blue-100 text-blue-800 rounded text-xs">
                      {currentUser.id === '1' ? 'admin' : 
                       currentUser.id === '2' ? 'user' : 
                       currentUser.id === '3' ? 'guest' : '不明'}
                    </span>
                  )}
                </p>
              </div>
            </div>

            {/* 簡易ドロップダウンメニュー */}
            <div className="absolute right-0 mt-2 w-48 rounded-md shadow-lg py-1 bg-white ring-1 ring-black ring-opacity-5 hidden group-hover:block">
              <a href="#profile" className="block px-4 py-2 text-sm text-secondary-700 hover:bg-secondary-100">
                プロフィール
              </a>
              <a href="#settings" className="block px-4 py-2 text-sm text-secondary-700 hover:bg-secondary-100">
                設定
              </a>
              <button
                onClick={handleLogout}
                className="block w-full text-left px-4 py-2 text-sm text-secondary-700 hover:bg-secondary-100"
              >
                ログアウト
              </button>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
